/*
* Title:    McKey_encrypted generator + McAddr_Generator
* Autor:    Antoine AUGAGNEUR
* Date:     march, 2022
* Sources:  > randHex function from https://codepen.io/code_monk/pen/kaWvPL
*
*/

//  random hex string generator
var randHex = function(len) {
  var maxlen = 8,
      min = Math.pow(16,Math.min(len,maxlen)-1) 
      max = Math.pow(16,Math.min(len,maxlen)) - 1,
      n   = Math.floor( Math.random() * (max-min+1) ) + min,
      r   = n.toString(16);
  while ( r.length < len ) {
     r = r + randHex( len - maxlen );
  }
  return r;
};

// randomly generated bigendian McKey_encrypted
var McKey_encrypted  = randHex(32);

// randomly generated bigendian McAddr
var McAddr_be = randHex(8);

// McAddr little endian conversion
var McAddr_le = McAddr_be[6]                            
                    + McAddr_be[7]
                    + McAddr_be[4]
                    + McAddr_be[5]
                    + McAddr_be[2]
                    + McAddr_be[3]
                    + McAddr_be[0]
                    + McAddr_be[1];

// ###############################################################################################################################################################

/*
* Title:    Keys derivation for LoRaWAN multicast session
* Autor:    Antoine AUGAGNEUR
* Date:     march, 2022
* Sources:  > LoRaWAN Remote Multicast Setup Specification v1.0.0 (Lora Alliance)
*           > Deciphering an EU868 LoRaWAN 1.0 OTAA Join Accept [online], available on:
*             https://runkit.com/avbentem/deciphering-a-lorawan-otaa-join-accept           
* 
* ## Definitions
* Device has:   GenAppKey               ( = AppKey, according to LoRaWAN 1.1)
* It receives:  McKey_encrypted         ( via McGroupSetupReq MAC command)
*               McAddr                  ( via McGroupSetupReq MAC command)
* Then,
* McRootKey = aes128_encrypt(GenAppKey, 0x00 | pad16)
* McKEKey = aes128_encrypt(McRootKey, 0x00 | pad16)
* McKey = aes128_decrypt(McKey_encrypted,McKEKey)
* And then,
* McAppSKey = aes128_encrypt(McKey, 0x01 | McAddr | pad16)
* McNetSKey = aes128_encrypt(McKey, 0x02 | McAddr | pad16)
*
* To be noted:
* The octet order over the air for all multi-octet fields is little endian (Least significant byte is 
* sent first). As a consequence, send data used for crypto operations are in little endian too.
* Howerver, logs device diplay them correctly.
*
*
* Information:
*           - inputs:   GenAppKey           (from node)                    
*                       McAddr              (from node, it has to be in little endian)   
*                       McKey_encrypted     (from node)
*           - outputs:  McRootKey
*                       McKEKey
*                       McKey
*                       McAppSKey         
*                       McNetSKey         
*                       
*
*/

// Initialization vector is always zero
var LORA_IV = CryptoJS.enc.Hex.parse('00000000000000000000000000000000');

// Encrypts the given buffer, returning another buffer.
function encrypt(buffer, key) {
    var ciphertext = CryptoJS.AES.encrypt(
        CryptoJS.lib.WordArray.create(buffer),
        CryptoJS.lib.WordArray.create(key),
        {
            mode: CryptoJS.mode.ECB,
            iv: LORA_IV,
            padding: CryptoJS.pad.NoPadding
        }
    ).ciphertext.toString(CryptoJS.enc.Hex);
    return new Buffer(ciphertext, 'hex');
}

// ## Known data (set or generated) #########################################
// GenAppKey (or AppKey) programmed in the device
// var GenAppKey = 'F255B7748A00FF7C22344C3402F6356F';
var GenAppKey = msg.payload;
GenAppKey = Buffer.from(GenAppKey.toString(16), 'hex');

// McKey_encrypted
// var McKey_encrypted = '6B46B8386CB3B7697B2674526E98209B';
McKey_encrypted = Buffer.from(McKey_encrypted.toString(16), 'hex');

// McAdrr
// var McAddr = 'F03D292B';
McAddr_le = Buffer.from(McAddr_le.toString(16), 'hex'); // normal address:F03D2928 --> little endian:28293DF0

// ## Key derivation to have McRootKey ######################################
// McRootKey = aes128_encrypt(GenAppKey, 0x00 | pad16)
var sKey_root = Buffer.from('000000000000000000000000000000', 'hex');
var McRootKey = encrypt(Buffer.concat([Buffer.from('00','hex'),sKey_root]),GenAppKey);

// ## Key derivation to have McKEKey
// McKEKey = aes128_encrypt(McRootKey, 0x00 | pad16)
var sKey_ke = Buffer.from('000000000000000000000000000000', 'hex');
var McKEKey = encrypt(Buffer.concat([Buffer.from('00','hex'),sKey_ke]),McRootKey);

// ## Key decryption to have McKey ##########################################
// McKey = aes128_decrypt(McKey_encrypted,McKEKey)
var McKey = encrypt(McKey_encrypted,McKEKey);


// ## Key derivation to have McAppsKey and McNetSKey ########################
// McAppSKey = aes128_encrypt(McKey, 0x01 | McAddr | pad16)
var sKey_apps = Buffer.concat([
                        McAddr_le,
                        Buffer.from('00000000000000000000000', 'hex')
                        ]);
var McAppSKey = encrypt(Buffer.concat([Buffer.from('01','hex'),sKey_apps]),McKey);

// McNetSKey = aes128_encrypt(McKey, 0x02 | McAddr | pad16)
var sKey_nets = Buffer.concat([
                        McAddr_le,
                        Buffer.from('00000000000000000000000', 'hex')
                        ]);
var McNetSKey = encrypt(Buffer.concat([Buffer.from('02','hex'),sKey_nets]),McKey);



// output

if (msg.payload.length == 32){

    msg.McAddrLE=McAddr_le.toString('hex');                // McAddr little endian
    msg.McAddrBE=McAddr_be;                             // McAddr big endian
    msg.McKey_ENC=McKey_encrypted.toString('hex');         // McKey_encrypted
    msg.McRootKEY=McRootKey.toString('hex');               // McRootKey
    msg.McKEKEY=McKEKey.toString('hex');                   // McKEKey
    msg.McKEY=McKey.toString('hex');                   // McKey
    msg.McAppSKEY=McAppSKey.toString('hex');               // McAppSKey
    msg.McNetSKEY=McNetSKey.toString('hex');               // McNetSKey

    msg.state = "1";

    return msg;
}
else
{
    msg.McAddrLE=McAddr_le.toString('hex');                // McAddr little endian
    msg.McAddrBE=McAddr_be.toString('hex');                             // McAddr big endian
    msg.McKey_ENC=McKey_encrypted.toString('hex');         // McKey_encrypted
    msg.McRootKEY="Error. Check the GenAppKey length";                              // McRootKey
    msg.McKEKEY="Error. Check the GenAppKey length";                                // McKEKey
    msg.McKEY="Error. Check the GenAppKey length";                   // McKey
    msg.McAppSKEY="Error. Check the GenAppKey length";                              // McAppSKey
    msg.McNetSKEY="Error. Check the GenAppKey length";                              // McNetSKey
    msg.state = "0";
    return msg;
}

/*
* Title:    Mc LoRaWAN command: McGroupSetupReq
* Autor:    Antoine AUGAGNEUR
* Date:     march, 2022
* Sources:  > LoRaWAN Application Layer Clock Synchronization Specification v1.0.0 (Lora Alliance)
*           > LoRaWAN Remote Multicast Setup Specification v1.0.0 (Lora Alliance)
*
* McGroupSetupReq payload:
*          | McGroupIDHeader  | McAddr    | McKey_encrypted   | minMcFCount   | maxMcFCount   | 
*          | 1 byte           | 4 bytes   | 16 bytes          | 4 bytes       | 4 bytes       |
*
* McGroupSetupReq command CID: 0x02
*
* Information:
*           - inputs:   McGroupID                    
*                       McAddr              (from node, it has to be in little endian)    
*                       McKey_encrypted     (from node)
*                       minMcFCount
*                       maxMcFCount
*           - outputs:  McGroupSetupReq
*                       minMcFCount (dec format)
*                       maxMcFCount (dec format)
*                       
*                                          
*/

// McGroupIDHeader (1 byte) / 6-bits RFU + 2-bits McGroupID
var RFU = '0';
// var McGroupID = '1'; // 0, 1, 2, or 3
var McGroupID = msg.payload.McGroupID;
var McGroupIDHeader = Buffer.from(RFU + McGroupID, 'hex');

// McAddr (4 byte)
// var McAddr = 'F03D292B';
var McAddr = msg.payload.McAddr;
McAddr = Buffer.from(McAddr, 'hex');

// McKey_encrypted (16 bytes)
// var McKey_encrypted = '6B46B8386CB3B7697B2674526E98209B';
var McKey_encrypted = msg.payload.McKey_encrypted;
McKey_encrypted = Buffer.from(McKey_encrypted, 'hex');

// minMcFCount (4 bytes)
// var minMcFCount = '00000000';
var minMcFCount = msg.payload.minMcFCount;
minMcFCount = minMcFCount.toString(16);
var minMcFCount_le = minMcFCount[6]                             // little endian conversion
                    + minMcFCount[7]
                    + minMcFCount[4]
                    + minMcFCount[5]
                    + minMcFCount[2]
                    + minMcFCount[3]
                    + minMcFCount[0]
                    + minMcFCount[1];
minMcFCount_le = Buffer.from(minMcFCount_le, 'hex');
var minMcFCount_dec = parseInt(minMcFCount,16);                 // passage en decimal

// maxMcFCount (4 bytes)
// var maxMcFCount = '0000000A'
var maxMcFCount = msg.payload.maxMcFCount;
maxMcFCount = maxMcFCount.toString(16);
var maxMcFCount_le = maxMcFCount[6]                             // little endian conversion
                    + maxMcFCount[7]
                    + maxMcFCount[4]
                    + maxMcFCount[5]
                    + maxMcFCount[2]
                    + maxMcFCount[3]
                    + maxMcFCount[0]
                    + maxMcFCount[1];
maxMcFCount_le = Buffer.from(maxMcFCount_le, 'hex');
var maxMcFCount_dec = parseInt(maxMcFCount,16);                 // passage en decimal


// McGroupSetupReq
var Command_02 = Buffer.from('02', 'hex');
var McGroupSetupReq = Buffer.concat([
                        Command_02,
                        McGroupIDHeader,
                        McAddr,
                        McKey_encrypted,
                        minMcFCount_le,
                        maxMcFCount_le                        
                        ]);

// ############################################################################


//msg.mcaddrlittle = McAddr.toString('hex');
//msg.mckeyencrypted = McKey_encrypted.toString('hex');
msg.McGroupSetupReq = McGroupSetupReq.toString('hex');



return msg;

/*
* Title:    Mc LoRaWAN command: McClassCSessionReq
* Autor:    Antoine AUGAGNEUR
* Date:     march, 2022
* Sources:  > LoRaWAN Application Layer Clock Synchronization Specification v1.0.0 (Lora Alliance)
*           > LoRaWAN Remote Multicast Setup Specification v1.0.0 (Lora Alliance)
*
* McClassCSessionReq payload:
*          | McGroupIDHeader  | SessionTime     | SessionTimeOut    | DLFrequ   | DR        | 
*          | 1 byte           | 4 bytes         | 1 byte            | 3 bytes   | 1 byte    |
*
* McClassCSessionReq command CID: 0x04
*
* Information:
*           - inputs:   McGroupID           
*                       SessionTime (Y,M,D,H,M,S)              
*                       TimeOut
*                       DLFreq
*                       DR
*           - outputs:  McClassCSessionReq
*                       Session Time (date format)
*                       SessionTimeOut (date format)
*                                          
*/

//  ## Date calculation
// GPS origin
var Origin = new Date('1980-01-06 00:00:00');
// Desired date
var Desired_date = Date.parse(msg.payload.SessionTime_date);        // Y, M, D // "parse" turns the date into the number of ms passed since 1970-01-01.
var Desired_time = Date.parse(msg.payload.SessionTime_time);        // H, M

// Difference calculation
var offset_time = Date.parse('1970-01-01 00:00:00');
var Desired = Desired_date + Desired_time - offset_time - Origin;   // time ( = hours and minuts) is set with an offset of 1970-01-01. So, I take the offset away.
Desired=Desired+7200000;    // 2 hours are added to synch with our time (UTC+01). Added twice because there are a Date(Y,M,D) setup AND a time (H,M) setup.
var Diff = Desired/1000;      // difference in seconds

// RFU (multiple use)
var RFU = '0';


// ## McClassCSessionReq definition -------------------------------------------------------------
// McGroupIDHeader (1 byte)
var McGroupID = msg.payload.McGroupID;                                              // To set ! (0,1,2 or 3)
var McGroupIDHeader = Buffer.from(RFU + McGroupID.toString(10), 'hex');

// Session Time (4 bytes)
var SessionTime = Diff.toString(16);       // hex to string conversion
var SessionTime_le = SessionTime[6]        // little endian conversion
                    + SessionTime[7]
                    + SessionTime[4]
                    + SessionTime[5]
                    + SessionTime[2]
                    + SessionTime[3]
                    + SessionTime[0]
                    + SessionTime[1];
SessionTime_le = Buffer.from(SessionTime_le, 'hex');

// SessionTimeOut 
var TimeOut_r = msg.payload.TimeOut;                                                     // To set ! (0 --> 15)
var TimeOut = parseInt(TimeOut_r,10);   
var TimeOut_hex = parseInt(TimeOut.toString(16), 16);
var TimeOutSec = 2**TimeOut; // 2^TimeOut
var SessionTimeOut = Buffer.from(RFU + TimeOut_hex.toString(16), 'hex');

// DLFreq (3 bytes)
var freq_r = msg.payload.DLFreq;
var freq = parseInt(freq_r,10);                                               // To set ! 
var freq_hex = parseInt((freq/100).toString(16), 16);       // dec to hex conversion
var freq_hex_str = freq_hex.toString(16);                   // hex to string conversion
var freq_hex_str_le = freq_hex_str[4]                       // little endian conversion
                    + freq_hex_str[5]
                    + freq_hex_str[2]
                    + freq_hex_str[3]
                    + freq_hex_str[0]
                    + freq_hex_str[1];
freq_hex_str_le = Buffer.from(freq_hex_str_le, 'hex');

// DR (1 byte)
var DR_r = msg.payload.DR;                                                         // To set ! 
var DR = parseInt(DR_r,10);
var DR_hex = parseInt(DR.toString(16), 16);                         // dec to hex conversion
var DR_hex_str = Buffer.from('0' + DR_hex.toString(16), 'hex');     // hex to string conversion
DR_hex_str = Buffer.from(DR_hex_str, 'hex');

// McClassCSessionReq
var Command_04 = Buffer.from('04', 'hex');
// var McClassCSessionReq = Command + McGroupIDHeader + SessionTime_le + SessionTimeOut + freq_hex_str_le + DR_hex_str;

var McClassCSessionReq = Buffer.concat([
                        Command_04,
                        McGroupIDHeader,
                        SessionTime_le,
                        SessionTimeOut,
                        freq_hex_str_le,
                        DR_hex_str                        
                        ]);






msg.desiredtime = Diff;
msg.McClassCSessionReq_command = McClassCSessionReq.toString('hex');


return msg;

