/*
* Title:    Multicast - Uplink analyzer
* Autor:    Antoine AUGAGNEUR
* Date:     march, 2022
* Sources:  LoRaWANÂ® Application Layer Clock Synchronization Specification
*
*/

// Uplink analysis
// DevEUI = msg.payload.DevEUI_uplink.DevEUI;                   //!\\ set your own path
upport = msg.payload.DevEUI_uplink.FPort;                       //!\\ set your own path
// Fcnt = msg.payload.DevEUI_uplink.FCntUp;                     //!\\ set your own path
message = msg.payload.DevEUI_uplink.payload_hex;                //!\\ set your own path



// Clock command analysis
if (upport == "200"){

    message = message.toString('hex');

    if (message[1] == "0"){
        msg.commandtype = "PackageVersionAns ";
        msg.analysis = "Pack.ID: " + message[2] + message[3] + " / " + "Pack.Ver: " +message[4] + message[5];
    }
    else if(message[1] == "1"){
        msg.commandtype = "McGroupStatusAns";
        var buf = "--";
        if (message.length > 4){buf = message[3];}
        if (message.length > 15){buf += "-" + message[15];}
        if (message.length > 27){buf += "-" + message[27];}
        if (message.length > 38){buf += "-" + message[38];}
        msg.analysis = "Nb.Group: " + message[2] + " / " + "Group defined: " + buf;
    }
    else if(message[1] == "2"){
        msg.commandtype = "McGroupSetupAns ";
        var grp = parseInt(message[3].toString(16),16);
        if (grp>3){ // bit IDerror == 1
            msg.analysis = "Setup group " + grp-4 + " aborted";
        }
        else{
            msg.analysis = "Setup group " + grp + " successful";
        }
    }
    else if(message[1] == "3"){
        msg.commandtype = "McGroupDeleteAns ";
        var grpb = parseInt(message[3].toString(16),16);
        if (grpb>3){ // bit IDerror == 1
            msg.analysis = "Delete group " + grpb-4 + " failed";
        }
        else{
            msg.analysis = "Delete group " + grpb + " successful";
        }
    }
    else if(message[1] == "4"){
        msg.commandtype = "McClassCSessionAns ";
        var grpc = parseInt(message[3].toString(16),16);
        if(grpc>10){
            msg.analysis = "Request aborted";
        }
        else{
            //var tps = message[8]+message[9]+message[6]+message[7]+message[4]+message[5];
            //var tps_dec =  parseInt(message[1].toString(16),16); // sec
            msg.analysis = "C session setup group " +  message[3];
        }
    }

    else{
        msg.commandtype = "...";
        msg.analysis = "...";
    }

}
else{
    msg.commandtype = "...";
    msg.analysis = "...";
}



return msg;
